mapper.select:
select DICT_TYPE_NM,DICT_TYPE_ID from T_BFS_DICT_TYPE where 1=1
and DICT_TYPE_ID = #{dictTypeId,jdbcType=VARCHAR}
AND DICT_TYPE_NM like ('%' || #{dictTypeNm,jdbcType=VARCHAR} || '%')

select count(*) from T_BFS_DICT_TYPE where 1=1
and DICT_TYPE_ID = #{dictTypeId,jdbcType=VARCHAR}
AND DICT_TYPE_NM like ('%' || #{dictTypeNm,jdbcType=VARCHAR} || '%')
