  and AUDIT_PRJ_ID = #{auditPrjId,jdbcType=VARCHAR}
  and VISIT_TAB_NM = #{visitTabNm,jdbcType=VARCHAR}

update SCM_BFS.T_BFS_USER_AUTH

  
    CREATE_USER = #{createUser,jdbcType=VARCHAR},
  
  
