mapper.select:
    select 
    
    from SCM_BFS.T_BFS_USER_AUTH
    where 1=1
     
      AND USER_ID = #{userId,jdbcType=VARCHAR}
     
     
      AND AUDIT_PRJ_ID = #{auditPrjId,jdbcType=VARCHAR}
     
     
      AND VISIT_TAB_NM = #{visitTabNm,jdbcType=VARCHAR}
     
     
      AND CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}
     
     
      AND CREATE_USER_NM like ('%'|| #{createUserNm,jdbcType=VARCHAR} || '%')
     
     
      AND TO_CHAR(CREATE_TM , 'YYYY-MM-DD HH:MI:SS') = #{createTm}

     
     ORDER BY CREATE_TM DESC

mapper.delete:
delete from SCM_BFS.T_BFS_USER_AUTH
where USER_ID = #{userId,jdbcType=VARCHAR}
  and AUDIT_PRJ_ID = #{auditPrjId,jdbcType=VARCHAR}
  and VISIT_TAB_NM = #{visitTabNm,jdbcType=VARCHAR}

mapper.insert:
insert into SCM_BFS.T_BFS_USER_AUTH (USER_ID, AUDIT_PRJ_ID, VISIT_TAB_NM, 
  CREATE_USER_ID, CREATE_USER_NM, CREATE_TM
  )
values (#{userId,jdbcType=VARCHAR}, #{auditPrjId,jdbcType=VARCHAR}, #{visitTabNm,jdbcType=VARCHAR}, 
  #{createUserId,jdbcType=VARCHAR}, #{createUserNm,jdbcType=VARCHAR}, sysdate
  )

mapper.update:
update SCM_BFS.T_BFS_USER_AUTH
set CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR},
  CREATE_USER_NM = #{createUserNm,jdbcType=VARCHAR},
  CREATE_TM = #{createTm,jdbcType=TIMESTAMP}
where USER_ID = #{userId,jdbcType=VARCHAR}
  and AUDIT_PRJ_ID = #{auditPrjId,jdbcType=VARCHAR}
  and VISIT_TAB_NM = #{visitTabNm,jdbcType=VARCHAR}

update SCM_BFS.T_BFS_USER_AUTH

  
    CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR},
  
  
    CREATE_USER_NM = #{createUserNm,jdbcType=VARCHAR},
  
  
    CREATE_TM = #{createTm,jdbcType=TIMESTAMP},
  

where USER_ID = #{userId,jdbcType=VARCHAR}
  and AUDIT_PRJ_ID = #{auditPrjId,jdbcType=VARCHAR}
  and VISIT_TAB_NM = #{visitTabNm,jdbcType=VARCHAR}
