INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0101','企业基础报表查询','企业基础报表查询','报表数据','企业决算数据的基本报表展示和查询：包括封面代码、主附表、该文档内报表分析模型涉及到的基础报表，共计62张报表。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0104','企业基础报表查询','企业基础报表查询','外部数据','外部数据包括工商数据、税务数据、互联网数据、电力数据。该部分内容互联网数据和电力数据只列出菜单接口，后续开发在功能扩展时执行。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0113','企业报表分析','总体经营状况','主指标区域结构-省内外区域结构','集团公司境内省市企业区域结构，包括集团各省市净资产、利润总额、企业数、上缴税费、总收入和总资产。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0114','企业报表分析','报表结构差异','资产负债差异性','在审计实务中，用偏离度反应资产负债，相对于行业平均值的离散程度，本表中队集团或子企业内偏离度由高到低排列。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0115','企业报表分析','报表结构差异','成本费用差异性','成本费用结构差异衡量标准用标准差来表示。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0116','企业报表分析','报表结构差异','合并报表完整性','合并报表完整性主要从集团与子企业关联关系层面对集团总体会计信息质量的完整性进行判断、分析。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0118','企业报表分析','重点事项分析','股权投资情况-投资重点或异常关注情况','根据股权投资或收购相关的久其附表，制定相关规则，筛选出重大或异常的投资项目或数据。主要通过股权投资现金净流、投资性质、投资收益、减值准备、投资并购及非主业投资方面，将重大或异常的股权投资或收购项目信息进行重点提示，为审计人员进行现场审计确定审计重点，提供审计线索。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0119','企业报表分析','重点事项分析','国有资产变动分析','针对固定资产投资和工程建设，先进行国有资产变动分析，对被审计单位当年国有资产变动情况表中资产评估减少、清产核资减少、产权界定减少、消化以前年度潜亏和挂账而减少、因自然灾害等不可抗拒因素减少、因主辅分离减少、资本（股本）折价、中央和地方政府确定的其他因素、经营减值各项目进行列示。反映集团各子公司国有资产变动整体情况。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0122','企业报表分析','重点事项分析','资产处置重点或异常关注情况','根据资产减值准备情况表相关数据，对集团公司减值准备进行趋势分析；以二级公司为维度进行结构分析；对集团公司子公司的本期减少额中的转销额进行排序；对减值准备余额的变动进行提示。确认相关变动是否与资产处置有关。根据对资产处置相关久其附表，将资产处置受让方异常、低价处置的项目进行分析提示。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0123','企业报表分析','重点事项分析','金融投资基本情况','针对央企久其财务报表中有关资本运作相关数据，统计列示反映期末资本运作事项（包括股票、期货、金融衍生品、委托理财等）、种类、主体、金额、浮动盈亏、年度间变化等情况。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0124','企业报表分析','重点事项分析','金融投资重点或异常关注情况','根据资本运作相关久其附表，一是提示关注审计期间新增的占比前30%资本运作事项，或审计人员根据实际情况在系统中自行确定重要性水平进行筛选；二是提示出现亏损、期末浮亏或者当年比上年收益减少的事项；三是提示境外资本运作事项。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0126','企业报表分析','重点事项分析','对外借款与担保重点或异常关注情况','根据对外借款与担保相关久其附表，将新增借款（担保）单位为民营企业或外资企业项目、未采取反担保措施的项目、出现损失的担保或借款项目、超过三年的预付款项项目、已到期未偿还的重大借款项目进行分析提示。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0128','企业报表分析','重点事项分析','职工薪酬重点或异常关注情况','通过基本情况，从集团内部子企业薪酬结构及其在指定期间内的变化两个维度对薪酬整体情况进行把握，并锁定异常子企业或异常年度。其次，挖掘重点审计对象存在的问题及线索，主要考虑可能存在的5个异常情况：企业规模和经营状况不匹配；效益不好但人均薪酬增加；利润下降但人均薪酬增加；薪酬结构异常；福利费超标。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0129','企业报表分析','重点事项分析','招待费基本情况','在费用方面，主要考虑招待费的主要支出情况。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0130','企业报表分析','重点事项分析','招待费重点或异常关注情况','以集团为一个整体，研究在指定年份所有子企业占比情况，并对占比前30%的企业进行重点关注，或审计人员根据实际情况在系统中自行确定重要性水平进行筛选。同时研究指定子企业招待费支出在审计期间的变化情况，对变化较大的企业进行重点关注。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0131','企业报表分析','重点事项分析','物资（服务）采购-采购变动异常','近年来中央企业为加强物资采购活动的管理，逐步加大集中采购的力度，上收物资采购的决策、审批、执行权力，物资集中采购比例成上升趋势。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0132','企业报表分析','重点事项分析','物资（服务）采购-采购总额大幅度增加','近年来中央企业为加强物资采购活动的管理，逐步加大集中采购的力度，上收物资采购的决策、审批、执行权力，物资集中采购比例成上升趋势。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0133','企业报表分析','重点事项分析','物资（服务）采购-采购比例变动异常','近年来中央企业为加强物资采购活动的管理，逐步加大集中采购的力度，上收物资采购的决策、审批、执行权力，物资集中采购比例成上升趋势。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0135','企业报表分析','重点事项分析','收入合规性判断-毛利率变动异常','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0136','企业报表分析','重点事项分析','收入合规性判断-盈利变动指数异常','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0137','企业报表分析','重点事项分析','收入合规性判断-关键指标变动幅度较大','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0138','企业报表分析','重点事项分析','收入合规性判断-收入与现金流变动不匹配','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0139','企业报表分析','重点事项分析','收入合规性判断-营业收入与应收账款不匹配','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0140','企业报表分析','重点事项分析','收入合规性判断-营业收入与营业利润变动异常','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0141','企业报表分析','重点事项分析','收入合规性判断-运费与营业收入/存货变动不匹配','收入合规性判断主要是从企业收入相关指标的变动来对企业营业的合理性、收入的合规性进行分析判断。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0146','企业报表分析','重点事项分析','非经营性损益分析-非经损益小于0','非经常性损益分析的主要关联路径是通过非经损益、转让损益、营业外支出、政府补助余额指标获得重点关注企业。首先，按非经损益小于0和转让损益小于0；其次营业外支出较大，非流动资产处置损失、债务重组损失、非货币资产交换损失，判断该值是否大于审计人员根据经验预先设定的重要性水平；如果政府补助余额较大但连续几年变动不大，判断期末余额或本期发生额是否在审计人员根据经验预先设定的重要性水平范围外。以上情况如果是，则为需要重点关注企业。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0147','企业报表分析','重点事项分析','非经营性损益分析-转让损益小于0','非经常性损益分析的主要关联路径是通过非经损益、转让损益、营业外支出、政府补助余额指标获得重点关注企业。首先，按非经损益小于0和转让损益小于0；其次营业外支出较大，非流动资产处置损失、债务重组损失、非货币资产交换损失，判断该值是否大于审计人员根据经验预先设定的重要性水平；如果政府补助余额较大但连续几年变动不大，判断期末余额或本期发生额是否在审计人员根据经验预先设定的重要性水平范围外。以上情况如果是，则为需要重点关注企业。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0148','企业报表分析','重点事项分析','非经营性损益分析-营业外支出较大','非经常性损益分析的主要关联路径是通过非经损益、转让损益、营业外支出、政府补助余额指标获得重点关注企业。首先，按非经损益小于0和转让损益小于0；其次营业外支出较大，非流动资产处置损失、债务重组损失、非货币资产交换损失，判断该值是否大于审计人员根据经验预先设定的重要性水平；如果政府补助余额较大但连续几年变动不大，判断期末余额或本期发生额是否在审计人员根据经验预先设定的重要性水平范围外。以上情况如果是，则为需要重点关注企业。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0150','企业报表分析','重点事项分析','科技创新能力分析','科技创新能力分析主要从高新技术行业科技投入不足角度出发进行识别需重点关注企业。首先，按国民经济行业分类与代码判断企业是否为高新技术行业；其次对高新技术行业的子企业，计算科技投入的比重、研究开发费用支出总额的比重，最后判断该比重是否小于审计人员根据经验预先设定的重要性水平，如果是，则为需要重点关注企业。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0151','企业报表分析','重点事项分析','专项储备分析','1 专项储备分析思路为对变动较大的进行提示，对减少数量与营业利润金额相当的进行提示。专项储备如果变动较大，表明企业很可能推动了大型项目、存在大型交易，这种变化的必要性、合理性、合法性值得审计人员关注。专项储备减少数量与营业利润金额相当表明企业有可能为了实现利润开展不合理或者非必要的针对专项储备的操作，审计人员应当同样关注这些企业是否存在刻意调整利润的动机以及背后的原因。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0178','企业审计模型','企业关系网络','通用关系网络','通用关系网络分析模型根据审计员现场导入数据模板可以展示相关数据之间的关系网络，并且可以根据需求进行配置。这些关系用企业关系网络图展示出来。图可以矢量缩放，节点和关系可以根据分析结果高亮突显。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0179','企业审计模型','企业关系网络','投资交易关系网络','现阶段根据工商税务数据可以分析出企业的投资关系和交易关系，并且能够根据交易金额大小和投资出入度可以找出中心节点和形成闭环的循环投资和交易。分成三种情况显示，一是投资和交易共同显示在企业关系网络图谱，二是单独显示投资关系或者交易关系。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0180','企业审计模型','企业审计模型','企业黑名单','该功能模块主要实现的是现场审计对黑名单的录入和查询功能，对已公开的黑名单企业名录所有用户都可以按照标签分类查询，对于新增加的企业名单需要提交系统，经有权人审核确认后可以录入到系统企业黑名单库，若是审核不通过则系统不予保存。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0181','企业审计模型','企业审计模型','敏感费用','通过工商数据、税务数据等，锁定重点关注事项、重大疑点和问题线索。重点关注业务招待费支出情况，关注有无景区开会、公款旅游、购买高档酒等违反八项规定问题，关注企业通过可疑中介变相违反八项规定，进行费用套现以及利益输送等问题。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0182','企业审计模型','企业审计模型','购销真实性','数据比对分析核实购销业务真实性，核实企业通过虚构交易逃避税，锁定重点关注事项、重大疑点和问题线索。','审计署','企业审计方法','SJSHU','QIYE')；
INSERT INTO T_DMS_AUDI_METH_TAB (METH_CD,ONE_NM,TWO_NM,METH_NM,METH_EXP,CREATE_USER_NM,CLASS_NM,ATTR_CLASS,MODE_CLASS) VALUES( 'Q0183','企业审计模型','企业审计模型','供应商依存度','分析企业物资采购业务，重点关注对特定央企依赖度高的企业，并进一步关注突击进入的中间商和供货商掩盖回扣行为等变种模型，经过分析锁定内外勾结、内部特定关系人盈利等违法违规行为，最终锁定重点关注事项、重大疑点和问题线索。','审计署','企业审计方法','SJSHU','QIYE')；